const preloader=document.getElementById("preloader");function preload(){preloader.style.opacity="0",preloader.style.zIndex="-99999",document.body.classList.remove("noScroll"),preloader.style.visibility="hidden"}const menuToggle=document.getElementById("navbarNavDropdown"),bsCollapse=bootstrap.Collapse.getOrCreateInstance(menuToggle,{toggle:!1});document.addEventListener("click",e=>{!menuToggle.contains(e.target)&&menuToggle.classList.contains("show")&&bsCollapse.toggle()});let mybutton=document.getElementById("btn-back-to-top");function scrollFunction(){document.body.scrollTop>40||document.documentElement.scrollTop>40?mybutton.style.display="block":mybutton.style.display="none"}function backToTop(){document.body.scrollTop=0,document.documentElement.scrollTop=0}window.onscroll=function(){scrollFunction()},mybutton.addEventListener("click",backToTop);