const preloader=document.getElementById("preloader");function preload(){preloader.style.opacity="0",preloader.style.zIndex="-99999",document.body.classList.remove("noScroll"),preloader.style.visibility="hidden"}const menuToggle=document.getElementById("navbarNavDropdown"),bsCollapse=bootstrap.Collapse.getOrCreateInstance(menuToggle,{toggle:!1});document.addEventListener("click",(e=>{menuToggle.contains(e.target)||menuToggle.classList.contains("show")&&bsCollapse.toggle()}));